class TodoList{todos;constructor(){this.todos=[]}addTodo(o){if("string"!=typeof o)throw new Error("Todo must be a string");this.todos.push(o)}removeTodo(o){if("number"!=typeof o)throw new Error("Index must be a number");if(o<0||o>=this.todos.length)throw new Error("Invalid index");this.todos.splice(o,1)}completeTodo(o){if("number"!=typeof o)throw new Error("Index must be a number");if(o<0||o>=this.todos.length)throw new Error("Invalid index");this.todos[o]=this.todos[o]+" (completed)"}printTodos(){console.log(this.todos)}printCompletedTodos(){console.log(this.todos.filter((o=>o.includes(" (completed)"))))}printPendingTodos(){console.log(this.todos.filter((o=>!o.includes(" (completed)"))))}getTodos(){return this.todos}setTodos(o){if(!Array.isArray(o))throw new Error("Todos must be an array");this.todos=o}clearTodos(){this.todos=[]}saveToFile(o){if("string"!=typeof o)throw new Error("Filename must be a string");const t=JSON.stringify(this.todos);fs.writeFile(o,t,(o=>{if(o)throw o;return"Todos saved to file"}))}loadFromFile(o){if("string"!=typeof o)throw new Error("Filename must be a string");fs.readFile(o,((o,t)=>{if(o)throw o;const s=JSON.parse(t);return this.setTodos(s),"Todos loaded from file"}))}}module.exports=TodoList;